mapper.update:
update tbl_goods set gdsName = #{gdsName}, cateCode = #{cateCode}, gdsPrice = #{gdsPrice}, gdsStock = #{gdsStock}, gdsDes = #{gdsDes},
gdsImg = #{gdsImg}, gdsThumbImg = #{gdsThumbImg}
where gdsNum = #{gdsNum}

	update tbl_order
   		set delivery = #{delivery}
	where orderId = #{orderId}

	update tbl_goods
   		set gdsStock = gdsStock - #{gdsStock}
	where gdsNum = #{gdsNum}
