mapper.delete:
	delete tbl_replydh
   		where repNum = #{repNum}
       		and memId = #{memId}

mapper.select:
	select memId
		from tbl_replydh
   			where repNum = #{repNum}

mapper.update:
update tbl_replydh
    set
        repCon = #{repCon}
    where repNum = #{repNum}
        and memId = #{memId}

mapper.delete:
delete tbl_cart
    where cartNum = #{cartNum}
        and memId = #{memId}
